import axios from "axios"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

const Edit = ()=>{

    const [fetching, setFetching] = useState("")
    useEffect(()=>{
        const fetchFunction = async () => {
      try {
        const response = await axios.get("http://localhost:5000/download");
        setFetching(response.data);
      } catch (e) {
        alert(e);
      }
    };
    fetchFunction()
    }, [])


    return(
        <section className="min-h-screen bg-darkblue">
            <nav className="h-[70px] bg-black text-white flex items-center justify-between px-10">
                <p>logo here</p>
                <ul className="flex gap-10">
                    <li><Link to="/admin">Admin Page</Link></li>
                    <li><Link to="/">Home Page</Link></li>
                </ul>
            </nav>
            <div className="w-11/12 mx-auto bg-green flex gap-16 flex-wrap">
                {fetching ? fetching.map((item)=> {
                    return <EditCard key={item.productName} name={item.productName} link={item.productDownloadLink} catergory={item.productCategory}/>
                }): <p>Loading</p>}
            </div>
        </section>
    )
}

const EditCard = ({name, catergory, link})=> {

    const productEditSubmit = (e)=> {
        e.preventDefault()
        console.log(e.target.formdata)
    }

    return(
        <div className="p-10 bg-lightblue rounded-xl">
            <form className="grid" onSubmit={productEditSubmit}>
            <label htmlFor="">Product Name</label>
            <input type="text" defaultValue={name} className="rounded-md py-1 px-3" />
            <label htmlFor="">Product Catergory</label>
            <input type="text" defaultValue={catergory} className="rounded-md py-1 px-3" />
            <label htmlFor="">Product Download Link</label>
            <input type="text" defaultValue={link} className="rounded-md py-1 px-3" />
            <button name={name}>Edit</button>
            </form>
        </div>
    )
}

export default Edit
