import axios from "axios"
import { useEffect, useState } from "react"
import { Link } from "react-router-dom"

const Edit = ()=>{

    const [fetching, setFetching] = useState("")
    useEffect(()=>{
        const fetchFunction = async () => {
      try {
        const response = await axios.get("http://localhost:5000/download");
        setFetching(response.data);
      } catch (e) {
        alert(e);
      }
    };
    fetchFunction()
    }, [])


    return(
        <section className="min-h-screen bg-darkblue">
            <nav className="h-[70px] bg-black text-white flex items-center justify-between px-10">
                <p>logo here</p>
                <ul className="flex gap-10">
                    <li><Link to="/admin">Admin Page</Link></li>
                    <li><Link to="/">Home Page</Link></li>
                </ul>
            </nav>
            <div className="w-11/12 mx-auto flex gap-14 flex-wrap p-16">
                {fetching ? fetching.map((item)=> {
                    return <EditCard key={item.productName} name={item.productName} link={item.productDownloadLink} category={item.productCategory}/>
                }): <p>Loading</p>}
            </div>
        </section>
    )
}

const EditCard = ({name, category, link})=> {

    const productEditSubmit = (e)=> {
        e.preventDefault()
        console.log(e.target)
    }

    return(
        <div className="p-6 bg-lightblue rounded-xl flex flex-col items-center text-white">
            <p className="text-2xl font-bold text-white">Name</p>
            <p className="font-semibold">{name}</p>
            <p className="text-2xl font-bold text-white">Category</p>
            <p className="font-semibold">{category}</p>
            <button name={name}>Edit</button>
        </div>
    )
}

export default Edit
